function cleanPlayer(playerPed)
	SetPedArmour(playerPed, 0)
	ClearPedBloodDamage(playerPed)
	ResetPedVisibleDamage(playerPed)
	ClearPedLastWeaponDamage(playerPed)
	ResetPedMovementClipset(playerPed, 0)
end
function setUniform(job, playerPed)
	TriggerEvent('skinchanger:getSkin', function(skin)
		if skin.sex == 0 then
			if job == 'uniforms' and Config.Uniforms[ESX.PlayerData.job.grade_name] then
				TriggerEvent('skinchanger:loadClothes', skin, Config.Uniforms[ESX.PlayerData.job.grade_name].male)
			elseif job == 'bullet_wear' and Config.Uniforms['bullet_wear'] then
				TriggerEvent('skinchanger:loadClothes', skin, Config.Uniforms['bullet_wear'].male)
			elseif job == 'kaos_wear' and Config.Uniforms['kaos_wear'] then
				TriggerEvent('skinchanger:loadClothes', skin, Config.Uniforms['kaos_wear'].male)
			else
				ESX.ShowNotification(_U('no_outfit'))
			end

			if job == 'bullet_wear' then
				SetPedArmour(playerPed, 100)
			end

			if job == 'gegana' then
				SetPedArmour(playerPed, 100)
			end

			if job == 'polri1' then
				SetPedArmour(playerPed, 100)
			end

			if job == 'polri2' then
				SetPedArmour(playerPed, 100)
			end
		else
			if job == 'uniforms' and Config.Uniforms[ESX.PlayerData.job.grade_name] then
				TriggerEvent('skinchanger:loadClothes', skin, Config.Uniforms[ESX.PlayerData.job.grade_name].female)
			elseif job == 'bullet_wear' and Config.Uniforms['bullet_wear'] then
				TriggerEvent('skinchanger:loadClothes', skin, Config.Uniforms['bullet_wear'].female)
			elseif job == 'kaos_wear' and Config.Uniforms['kaos_wear'] then
				TriggerEvent('skinchanger:loadClothes', skin, Config.Uniforms['kaos_wear'].female)
			else
				ESX.ShowNotification(_U('no_outfit'))
			end

			if job == 'bullet_wear' then
				SetPedArmour(playerPed, 100)
			end
			
			if job == 'gegana' then
				SetPedArmour(playerPed, 100)
			end

			if job == 'polri1' then
				SetPedArmour(playerPed, 100)
			end

			if job == 'polri2' then
				SetPedArmour(playerPed, 100)
			end
		end
	end)
end

function OpenCloakroomMenu()
	local playerPed = PlayerPedId()
	local grade = ESX.PlayerData.job.grade_name

	local elements = {
		{ label = _U('citizen_wear'), value = 'citizen_wear' },
		{ label = _U('bullet_wear'), value = 'bullet_wear' },
		{ label = _U('kaos_wear'), value = 'kaos_wear' },
		{ label = _U('police_wear'), value = 'uniforms' }
	}


	ESX.UI.Menu.CloseAll()

	ESX.UI.Menu.Open('default', GetCurrentResourceName(), 'cloakroom', {
		title    = _U('cloakroom'),
		align    = 'bottom-right',
		elements = elements
	}, function(data, menu)
		cleanPlayer(playerPed)

		if data.current.value == 'citizen_wear' then
			if Config.EnableNonFreemodePeds then
				ESX.TriggerServerCallback('esx_skin:getPlayerSkin', function(skin, jobSkin)
					local isMale = skin.sex == 0

					TriggerEvent('skinchanger:loadDefaultModel', isMale, function()
						ESX.TriggerServerCallback('esx_skin:getPlayerSkin', function(skin)
							TriggerEvent('skinchanger:loadSkin', skin)
							TriggerEvent('esx:restoreLoadout')
						end)
					end)

				end)
			else
				ESX.TriggerServerCallback('esx_skin:getPlayerSkin', function(skin)
					TriggerEvent('skinchanger:loadSkin', skin)
				end)
			end

			if Config.MaxInService ~= -1 then
				ESX.TriggerServerCallback('esx_service:isInService', function(isInService)
					if isInService then
						playerInService = false

						local notification = {
							title    = _U('service_anonunce'),
							subject  = '',
							msg      = _U('service_out_announce', GetPlayerName(PlayerId())),
							iconType = 1
						}

						TriggerServerEvent('esx_service:notifyAllInService', notification, 'police')

						TriggerServerEvent('esx_service:disableService', 'police')
						ESX.ShowNotification(_U('service_out'))
					end
				end, 'police')
			end
		end

		if data.current.value ~= 'citizen_wear' then
			setUniform(data.current.value ,playerPed)
		end

		if data.current.value == 'freemode_ped' then
			local modelHash = ''

			ESX.TriggerServerCallback('esx_skin:getPlayerSkin', function(skin, jobSkin)
				if skin.sex == 0 then
					modelHash = GetHashKey(data.current.maleModel)
				else
					modelHash = GetHashKey(data.current.femaleModel)
				end

				ESX.Streaming.RequestModel(modelHash, function()
					SetPlayerModel(PlayerId(), modelHash)
					SetModelAsNoLongerNeeded(modelHash)

					TriggerEvent('esx:restoreLoadout')
				end)
			end)
		end
	end, function(data, menu)
		menu.close()
	end)
end

