function OpenVehicleSpawnerMenu(type, station, part, partNum)
	local playerCoords = GetEntityCoords(PlayerPedId())
	local elements = {
		{label = _U('garage_storeditem2'), action = 'garage'},
		{label = _U('garage_storeitem2'), action = 'store_garage'},
		{label = _U('garage_buyitem2'), action = 'buy_vehicle'}
	}

	ESX.UI.Menu.Open('default', GetCurrentResourceName(), 'vehicle', {
		title    = _U('garage_title2'),
		align    = 'bottom-right',
		elements = elements
	}, function(data, menu)
		if data.current.action == 'buy_vehicle' then
			local shopElements, shopCoords = {}

			if type == 'car' then
				shopCoords = Config.PoliceStations[station].Vehicles[partNum].InsideShop
				local authorizedVehicles = Config.AuthorizedVehicles2[ESX.PlayerData.job.grade_name]

				if #Config.AuthorizedVehicles2.Shared > 0 then
					for k,vehicle in ipairs(Config.AuthorizedVehicles2.Shared) do
						table.insert(shopElements, {
							label = ('%s - <span style="color:green;">%s</span>'):format(vehicle.label, _U('shop_item', ESX.Math.GroupDigits(vehicle.price))),
							name  = vehicle.label,
							model = vehicle.model,
							price = vehicle.price,
							type  = 'car'
						})
					end
				end

				if #authorizedVehicles > 0 then
					for k,vehicle in ipairs(authorizedVehicles) do
						table.insert(shopElements, {
							label = ('%s - <span style="color:green;">%s</span>'):format(vehicle.label, _U('shop_item', ESX.Math.GroupDigits(vehicle.price))),
							name  = vehicle.label,
							model = vehicle.model,
							price = vehicle.price,
							type  = 'car'
						})
					end
				else
					if #Config.AuthorizedVehicles2.Shared == 0 then
						return
					end
				end
			elseif type == 'helicopter' then
				shopCoords = Config.PoliceStations[station].Helicopters[partNum].InsideShop
				local authorizedHelicopters = Config.AuthorizedHelicopters[ESX.PlayerData.job.grade_name]

				if #authorizedHelicopters > 0 then
					for k,vehicle in ipairs(authorizedHelicopters) do
						table.insert(shopElements, {
							label = ('%s - <span style="color:green;">%s</span>'):format(vehicle.label, _U('shop_item', ESX.Math.GroupDigits(vehicle.price))),
							name  = vehicle.label,
							model = vehicle.model,
							price = vehicle.price,
							livery = vehicle.livery or nil,
							type  = 'helicopter'
						})
					end
				else
					ESX.ShowNotification(_U('helicopter_notauthorized'))
					return
				end
			end

			OpenShopMenu(shopElements, playerCoords, shopCoords)
		elseif data.current.action == 'garage' then
			local garage = {}

			ESX.TriggerServerCallback('esx_vehicleshop:retrieveJobVehicles', function(jobVehicles)
				if #jobVehicles > 0 then
					for k,v in ipairs(jobVehicles) do
						local props = json.decode(v.vehicle)
						local vehicleName = GetLabelText(GetDisplayNameFromVehicleModel(props.model))
						local label = ('%s - <span style="color:darkgoldenrod;">%s</span>: '):format(vehicleName, props.plate)

						if v.stored then
							label = label .. ('<span style="color:green;">%s</span>'):format(_U('garage_stored'))
						else
							label = label .. ('<span style="color:darkred;">%s</span>'):format(_U('garage_notstored'))
						end

						table.insert(garage, {
							label = label,
							stored = v.stored,
							model = props.model,
							vehicleProps = props
						})
					end

					ESX.UI.Menu.Open('default', GetCurrentResourceName(), 'vehicle_garage', {
						title    = _U('garage_title'),
						align    = 'bottom-right',
						elements = garage
					}, function(data2, menu2)
						if data2.current.stored then
							local foundSpawn, spawnPoint = GetAvailableVehicleSpawnPoint(station, part, partNum)

							if foundSpawn then
								menu2.close()

								ESX.Game.SpawnVehicle(data2.current.model, spawnPoint.coords, spawnPoint.heading, function(vehicle)
									ESX.Game.SetVehicleProperties(vehicle, data2.current.vehicleProps)

									TriggerServerEvent('esx_vehicleshop:setJobVehicleState', data2.current.vehicleProps.plate, true)
									ESX.ShowNotification(_U('garage_released'))
								end)
							end
						else
							ESX.ShowNotification(_U('garage_notavailable'))
						end
					end, function(data2, menu2)
						menu2.close()
					end)
				else
					ESX.ShowNotification(_U('garage_empty'))
				end
			end, type)
		elseif data.current.action == 'store_garage' then
			StoreNearbyVehicle(playerCoords)
		end
	end, function(data, menu)
		menu.close()
	end)
end

function StoreNearbyVehicle(playerCoords)
	local vehicles, vehiclePlates = ESX.Game.GetVehiclesInArea(playerCoords, 30.0), {}

	if #vehicles > 0 then
		for k,v in ipairs(vehicles) do

			-- Make sure the vehicle we're saving is empty, or else it wont be deleted
			if GetVehicleNumberOfPassengers(v) == 0 and IsVehicleSeatFree(v, -1) then
				table.insert(vehiclePlates, {
					vehicle = v,
					plate = ESX.Math.Trim(GetVehicleNumberPlateText(v))
				})
			end
		end
	else
		ESX.ShowNotification(_U('garage_store_nearby'))
		return
	end

	ESX.TriggerServerCallback('great_polisi:storeNearbyVehicle', function(storeSuccess, foundNum)
		if storeSuccess then
			local vehicleId = vehiclePlates[foundNum]
			local attempts = 0
			ESX.Game.DeleteVehicle(vehicleId.vehicle)
			IsBusy = true

			Citizen.CreateThread(function()
				BeginTextCommandBusyString('STRING')
				AddTextComponentSubstringPlayerName(_U('garage_storing'))
				EndTextCommandBusyString(4)

				while IsBusy do
					Citizen.Wait(100)
				end

				RemoveLoadingPrompt()
			end)

			-- Workaround for vehicle not deleting when other players are near it.
			while DoesEntityExist(vehicleId.vehicle) do
				Citizen.Wait(500)
				attempts = attempts + 1

				-- Give up
				if attempts > 30 then
					break
				end

				vehicles = ESX.Game.GetVehiclesInArea(playerCoords, 30.0)
				if #vehicles > 0 then
					for k,v in ipairs(vehicles) do
						if ESX.Math.Trim(GetVehicleNumberPlateText(v)) == vehicleId.plate then
							ESX.Game.DeleteVehicle(v)
							break
						end
					end
				end
			end

			IsBusy = false
			ESX.ShowNotification(_U('garage_has_stored'))
		else
			ESX.ShowNotification(_U('garage_has_notstored'))
		end
	end, vehiclePlates)
end

function GetAvailableVehicleSpawnPoint(station, part, partNum)
	local spawnPoints = Config.PoliceStations[station][part][partNum].SpawnPoints
	local found, foundSpawnPoint = false, nil

	for i=1, #spawnPoints, 1 do
		if ESX.Game.IsSpawnPointClear(spawnPoints[i].coords, spawnPoints[i].radius) then
			found, foundSpawnPoint = true, spawnPoints[i]
			break
		end
	end

	if found then
		return true, foundSpawnPoint
	else
		ESX.ShowNotification(_U('vehicle_blocked'))
		return false
	end
end

function OpenShopMenu(elements, restoreCoords, shopCoords)
	local playerPed = PlayerPedId()
	isInShopMenu = true

	ESX.UI.Menu.Open('default', GetCurrentResourceName(), 'vehicle_shop', {
		title    = _U('vehicleshop_title'),
		align    = 'bottom-right',
		elements = elements
	}, function(data, menu)
		ESX.UI.Menu.Open('default', GetCurrentResourceName(), 'vehicle_shop_confirm', {
			title    = _U('vehicleshop_confirm', data.current.name, data.current.price),
			align    = 'bottom-right',
			elements = {
				{label = _U('confirm_no'), value = 'no'},
				{label = _U('confirm_yes'), value = 'yes'}
		}}, function(data2, menu2)
			if data2.current.value == 'yes' then
				local newPlate = exports['lsi_vehicle']:GeneratePlate()
				local vehicle  = GetVehiclePedIsIn(playerPed, false)
				local props    = ESX.Game.GetVehicleProperties(vehicle)
				props.plate    = newPlate

				ESX.TriggerServerCallback('great_polisi:buyJobVehicle', function (bought)
					if bought then
						ESX.ShowNotification(_U('vehicleshop_bought', data.current.name, ESX.Math.GroupDigits(data.current.price)))

						isInShopMenu = false
						ESX.UI.Menu.CloseAll()
						DeleteSpawnedVehicles()
						FreezeEntityPosition(playerPed, false)
						SetEntityVisible(playerPed, true)

						ESX.Game.Teleport(playerPed, restoreCoords)
					else
						ESX.ShowNotification(_U('vehicleshop_money'))
						menu2.close()
					end
				end, props, data.current.type)
			else
				menu2.close()
			end
		end, function(data2, menu2)
			menu2.close()
		end)
	end, function(data, menu)
		isInShopMenu = false
		ESX.UI.Menu.CloseAll()

		DeleteSpawnedVehicles()
		FreezeEntityPosition(playerPed, false)
		SetEntityVisible(playerPed, true)

		ESX.Game.Teleport(playerPed, restoreCoords)
	end, function(data, menu)
		DeleteSpawnedVehicles()
		WaitForVehicleToLoad(data.current.model)

		ESX.Game.SpawnLocalVehicle(data.current.model, shopCoords, 0.0, function(vehicle)
			table.insert(spawnedVehicles, vehicle)
			TaskWarpPedIntoVehicle(playerPed, vehicle, -1)
			FreezeEntityPosition(vehicle, true)
			SetModelAsNoLongerNeeded(data.current.model)

			if data.current.livery then
				SetVehicleModKit(vehicle, 0)
				SetVehicleLivery(vehicle, data.current.livery)
			end
		end)
	end)

	WaitForVehicleToLoad(elements[1].model)
	ESX.Game.SpawnLocalVehicle(elements[1].model, shopCoords, 0.0, function(vehicle)
		table.insert(spawnedVehicles, vehicle)
		TaskWarpPedIntoVehicle(playerPed, vehicle, -1)
		FreezeEntityPosition(vehicle, true)
		SetModelAsNoLongerNeeded(elements[1].model)

		if elements[1].livery then
			SetVehicleModKit(vehicle, 0)
			SetVehicleLivery(vehicle, elements[1].livery)
		end
	end)
end

Citizen.CreateThread(function()
	while true do
		Citizen.Wait(0)

		if isInShopMenu then
			DisableControlAction(0, 75, true)  -- Disable exit vehicle
			DisableControlAction(27, 75, true) -- Disable exit vehicle
		else
			Citizen.Wait(500)
		end
	end
end)

function DeleteSpawnedVehicles()
	while #spawnedVehicles > 0 do
		local vehicle = spawnedVehicles[1]
		ESX.Game.DeleteVehicle(vehicle)
		table.remove(spawnedVehicles, 1)
	end
end

function WaitForVehicleToLoad(modelHash)
	modelHash = (type(modelHash) == 'number' and modelHash or GetHashKey(modelHash))

	if not HasModelLoaded(modelHash) then
		RequestModel(modelHash)

		BeginTextCommandBusyString('STRING')
		AddTextComponentSubstringPlayerName(_U('vehicleshop_awaiting_model'))
		EndTextCommandBusyString(4)

		while not HasModelLoaded(modelHash) do
			Citizen.Wait(0)
			DisableAllControlActions(0)
		end

		RemoveLoadingPrompt()
	end
end

AddEventHandler('cenz_policejob:TargetAction', function(data)
	local ped = data.entity
	local type = data.type
	local playerPed = PlayerPedId()
	local targetserverid = 0

	for k,player in ipairs(GetActivePlayers()) do
		local tped = GetPlayerPed(player)

		if DoesEntityExist(tped) and (tped ~= playerPed and ped == tped) then
			targetserverid = GetPlayerServerId(player)
		end
	end

	if type == 'body_search' then
		ExecuteCommand ("me Menggeledah")
		ExecuteCommand ("e nervous2")
		TriggerServerEvent('great_polisi:message', targetserverid, _U('being_searched'))
		exports['es_extended']:OpenTargetInventory()
	elseif type == 'handcuff' then
		ExecuteCommand ("me Memborgol")
		local playerheading = GetEntityHeading(playerPed)
		local playerlocation = GetEntityForwardVector(playerPed)
		local playerCoords = GetEntityCoords(playerPed)
		TriggerServerEvent('great_polisi:requestarrest', targetserverid, playerheading, playerCoords, playerlocation)
		exports['lsi_base']:SendAlert('inform', 'Memborgol')
	elseif type == 'uncuff' then
		ExecuteCommand ("me Melepas Borgol")
		local playerheading = GetEntityHeading(playerPed)
		local playerlocation = GetEntityForwardVector(playerPed)
		local playerCoords = GetEntityCoords(playerPed)
		TriggerServerEvent('great_polisi:requestrelease', targetserverid, playerheading, playerCoords, playerlocation)
		exports['lsi_base']:SendAlert('inform', 'Membuka borgol')
	elseif type == 'drag' then
		ExecuteCommand ("me Menyeret")
		TriggerServerEvent('great_polisi:drag', targetserverid)
		exports['lsi_base']:SendAlert('inform', 'Menyeret')
	elseif type == 'put_in_vehicle' then
		ExecuteCommand ("me Memasukkan Kedalam Kendaraan")
		TriggerServerEvent('great_polisi:putInVehicle', targetserverid)
		exports['lsi_base']:SendAlert('inform', 'Memasukan paksa')
	elseif type == 'unpaid_bills' then
		ExecuteCommand ("me Mengecek Denda Yang Belum Terbayarkan")
		OpenUnpaidBillsMenu(targetserverid)
	elseif type == 'communityservice' then
		SendToCommunityService(targetserverid)
	elseif type == 'license' then
		ExecuteCommand ("me Mengecek Surat-surat")
		ShowPlayerLicense(targetserverid)
	elseif type == 'out_the_vehicle' then
		local closestPlayer, closestDistance = ESX.Game.GetClosestPlayer()
		if closestPlayer ~= -1 and closestDistance <= 3.0 then
			ExecuteCommand ("me Mengeluarkan Dari Kendaraan")
			TriggerServerEvent('great_polisi:OutVehicle', targetserverid)
		end
	elseif type == 'jail_menu' then
		TriggerEvent("esx-qalle-jail:openJailMenu")
	elseif type == 'vehicle_infos' then
		local vehicleData = ESX.Game.GetVehicleProperties(ped)
		OpenVehicleInfosMenu(vehicleData)
	elseif type == 'hijack' then
		local timeout = 20
		local vehicle = ped

		if IsPedSittingInAnyVehicle(playerPed) then
			exports['lsi_modernui']:Alert("Kepolisian", "Jangan ada yang di dalam kendaraan", 5000, 'info')
		else
			NetworkRequestControlOfEntity(vehicle)

			while not NetworkHasControlOfEntity(vehicle) and timeout > 0 do 
				NetworkRequestControlOfEntity(vehicle)
				Citizen.Wait(100)
				timeout = timeout -1
			end

			exports['lsi_base']:Progress({
				name = "hijack_vehicle",
				duration = 20000,
				label = 'Membobol',
				useWhileDead = true,
				canCancel = false,
				controlDisables = {
					disableMovement = true,
					disableCarMovement = true,
					disableMouse = false,
					disableCombat = true,
				},
				animation = {
					task = "WORLD_HUMAN_WELDING",
				},
			}, function(cancelled)
				if not cancelled then
					SetVehicleDoorsLocked(vehicle, 1)
					SetVehicleDoorsLockedForAllPlayers(vehicle, false)
					exports['lsi_modernui']:Alert("Kepolisian", "Kendaraan terbuka", 5000, 'info')
				end
			end)
		end
	elseif type == 'impound' then
		local vehicle = ped
		local coords = GetEntityCoords(vehicle)
		if currentTask.busy then
			return
		end

		ESX.ShowHelpNotification(_U('impound_prompt'))
		TaskStartScenarioInPlace(playerPed, 'CODE_HUMAN_MEDIC_TEND_TO_DEAD', 0, true)

		currentTask.busy = true
		currentTask.task = ESX.SetTimeout(10000, function()
			ClearPedTasks(playerPed)
			ImpoundVehicle(vehicle)
			Citizen.Wait(100) -- sleep the entire script to let stuff sink back to reality
		end)

		-- keep track of that vehicle!
		Citizen.CreateThread(function()
			while currentTask.busy do
				Citizen.Wait(1000)

				vehicle = GetClosestVehicle(coords.x, coords.y, coords.z, 3.0, 0, 71)
				if not DoesEntityExist(vehicle) and currentTask.busy then
					ESX.ShowNotification(_U('impound_canceled_moved'))
					ESX.ClearTimeout(currentTask.task)
					ClearPedTasks(playerPed)
					currentTask.busy = false
					break
				end
			end
		end)
	elseif type == 'impound_samsat' then
		TriggerEvent("nz:openimpoudmenu")
	end
end)

